-- ----------------------------
-- Table structure for `mbg_daily`
-- ----------------------------
DROP TABLE IF EXISTS `mbg_daily`;
CREATE TABLE `mbg_daily` (
  `id` int(5) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `type` int(5) unsigned NOT NULL,
  `content` text NOT NULL,
  `c_time` varchar(255) NOT NULL,
  `author` varchar(50) NOT NULL DEFAULT '不详',
  `permission` tinyint(1) unsigned NOT NULL,
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态：1正常；0：删除',
  `interview` int(50) unsigned DEFAULT '0' COMMENT '访问量',
  `is_recommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0：不推荐，1：推荐',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='发布的文章记录表';

-- ----------------------------
