-- ----------------------------
-- Table structure for `mbg_leave_message`
-- ----------------------------
DROP TABLE IF EXISTS `mbg_leave_message`;
CREATE TABLE `mbg_leave_message` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` text NOT NULL,
  `creat_date` int(11) NOT NULL,
  PRIMARY KEY (`id`)
