-- ----------------------------
-- Table structure for `mbg_photo_album`
-- ----------------------------
DROP TABLE IF EXISTS `mbg_photo_album`;
CREATE TABLE `mbg_photo_album` (
  `id` int(5) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT '相册名称',
  `typeid` int(5) unsigned NOT NULL COMMENT '来源于mbg_photo_type表中的id；相册所存相片类别',
  `typename` varchar(50) NOT NULL COMMENT '相册类别名称',
  `authority` tinyint(1) unsigned DEFAULT '0' COMMENT '权限（0:仅自己可见；1：公开）',
  `description` text COMMENT '相册，描述',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已被删除（0：否；1：已删除）',
  `cover` varchar(255) DEFAULT '' COMMENT '相册封面图片路径',
  `c_time` varchar(255) NOT NULL COMMENT '创建相册的时间戳',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐:默认0：否；1：是',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

